String.prototype.format = function () {
    var args = arguments;
    return this.replace(/\{(\d+)\}/g,
				function (m, i) {
				    return args[i];
				});
}

String.prototype.trim = function () {
    return this.replace(/(^\s*)|(\s*$)/g, "");
}

JSON2CSharp = {
    _allClass: [],
    _genClassCode: function (obj, name) {
        var clas = "public class {0}\r\n{\r\n".format(name || "Root");
        for (var n in obj) {
            var v = obj[n];
            n = n.trim();
            clas += "    {0}    public {1} {2} { get; set; }\n".format(this._genComment(v), this._genTypeByProp(n, v), n);
        }
        clas += "}\n";
        this._allClass.push(clas);
        return this._allClass.join("\n");
    },
    _genTypeByProp: function (name, val) {
        switch (Object.prototype.toString.apply(val)) {
            case "[object Number]":
                {
                    return val.toString().indexOf(".") > -1 ? "double" : "int";
                }
            case "[object Date]":
                {
                    return "DateTime";
                }
            case "[object Object]":
                {
                    name = name.substring(0, 1).toUpperCase() + name.substring(1);
                    this._genClassCode(val, name);
                    return name;
                }
            case "[object Array]":
                {
                    return "List <{0}>".format(this._genTypeByProp(name + "Item", val[0]));
                }
            default:
                {
                    return "string";
                }
        }
    },
    _genComment: function (val) {
    	//return "";
       var commm = typeof (val) == "string" && /.*[\u4e00-\u9fa5]+.*$/.test(val) ? val : "";
        return "/// <summary>\r\n    /// " + commm + "\r\n    /// </summary>\r\n";
    },
    convert: function (jsonObj) {
        this._allClass = [];
        return this._genClassCode(jsonObj);
    }
}

function JSON2CC(str){
	var v = eval("(" + str + ")");
	var res = JSON2CSharp.convert(v);
	return res;
}

function JSONFormat(txt, compress) { /* 格式化JSON源码(对象转换为JSON文本) */
				var indentChar = '    ';
				if(/^\s*$/.test(txt)) {
					return '数据为空,无法格式化! ';
				}
				try {
					var data = eval('(' + txt + ')');
				} catch(e) {
					return '数据源语法错误,格式化失败! ';
				};
				var draw = [],
					last = false,
					This = this,
					line = compress ? '' : '\n',
					nodeCount = 0,
					maxDepth = 0;

				var notify = function(name, value, isLast, indent /*缩进*/ , formObj) {
					nodeCount++; /*节点计数*/
					for(var i = 0, tab = ''; i < indent; i++) tab += indentChar; /* 缩进HTML */
					tab = compress ? '' : tab; /*压缩模式忽略缩进*/
					maxDepth = ++indent; /*缩进递增并记录*/
					if(value && value.constructor == Array) { /*处理数组*/
						draw.push(tab + (formObj ? ('"' + name + '":') : '') + '[' + line); /*缩进'[' 然后换行*/
						for(var i = 0; i < value.length; i++)
							notify(i, value[i], i == value.length - 1, indent, false);
						draw.push(tab + ']' + (isLast ? line : (',' + line))); /*缩进']'换行,若非尾元素则添加逗号*/
					} else if(value && typeof value == 'object') { /*处理对象*/
						draw.push(tab + (formObj ? ('"' + name + '":') : '') + '{' + line); /*缩进'{' 然后换行*/
						var len = 0,
							i = 0;
						for(var key in value) len++;
						for(var key in value) notify(key, value[key], ++i == len, indent, true);
						draw.push(tab + '}' + (isLast ? line : (',' + line))); /*缩进'}'换行,若非尾元素则添加逗号*/
					} else {
						if(typeof value == 'string') value = '"' + value + '"';
						draw.push(tab + (formObj ? ('"' + name + '":') : '') + value + (isLast ? '' : ',') + line);
					};
				};
				var isLast = true,
					indent = 0;
				notify('', data, isLast, indent, false);
				return draw.join('');
			}
